mapper.update:

	update question
	set question_answer_state = '답변완료'
	where question_no = #{question_no}

mapper.select:

select ra.RANDOMBOX_PK, ra.itemlist_no, ra.chance  from RANDOMBOX_ALL ra, ITEMLIST it
where ra.itemlist_no=it.ITEMLIST_NO
order by RANDOMBOX_PK asc;

mapper.update:

update ITEMPACKAGE 
SET ITEMLIST_NO = #{ITEMLIST_NO}
WHERE PACKAGEPK = #{PACKAGEPK}


update RANDOMBOX_ALL
SET CHANCE = #{chance}
WHERE RANDOMBOX_PK = #{randombox_pk}


update RANDOMBOX_ALL
SET CHANCE = #{chance}
WHERE RANDOMBOX_PK = #{randombox_pk}


update RANDOMBOX_ALL
SET CHANCE = #{chance}
WHERE RANDOMBOX_PK = #{randombox_pk}
