mapper.select:

	
		select *
		from (
			select rownum as rn, AG.*
			from (
				select AGENCY_NO, AGENCY_WRITER, AGENCY_TITLE, LINK2_NO, 
						AGENCY_TYPE, AGENCY_LOC, AGENCY_STARTDATE, 
						AGENCY_ENDDATE, AGENCY_ENROLLDATE, AGENCY_PAYTYPE, 
						AGENCY_PAY, AGENCY_STATUS, AGENCY_CONTENT, 
						AGENCY_VEIWS, AGENCY_KEYWORD, AGENCY_OPTION, 
						MEMBER_ID, SCORE_BUY, SCORE_BUY_COUNT, SCORE_BUY_RATE, 
						SCORE_SELL, SCORE_SELL_COUNT, SCORE_SELL_RATE, ITEMFILENAME
				from AGENCY, SCORE, (
					select ITEMFILENAME 
					from ITEMLIST
					where ITEMLIST_NO = (
						select ITEMLIST_NO
						from MY_ITEM
						where MYITEM_NO = (
							select MYITEM_NO 
							from (
								select MYITEM_NO 
								from (
									select MYITEM_NO 
									from USING_ITEM 
									where MYITEM_NO 
									in (
										select MYITEM_NO 
										from MY_ITEM, agency 
										where MEMBER_ID = agency_writer 
										and ITEMLIST_NO 
										in (
											select ITEMLIST_NO 
											from itemlist 
											where ITEMTYPE = 2 
											and MYITEM_STATUS ! = 2
										)
									)
								) order by rownum desc
							) where rownum= 1
						)
					)
				)
				where AGENCY_TITLE like '%'||#{agency_title}||'%' 
				and agency_type = 1 
				and agency_writer = member_id 
				order by AGENCY_NO desc
			) AG 
		)
		where rn between #{masterPageBegin} and #{masterPageEnd}


	
		select *
		from (
			select rownum as rn, AG.*
			from (
				select AGENCY_NO, AGENCY_WRITER, AGENCY_TITLE, LINK2_NO, 
						AGENCY_TYPE, AGENCY_LOC, AGENCY_STARTDATE, 
						AGENCY_ENDDATE, AGENCY_ENROLLDATE, AGENCY_PAYTYPE, 
						AGENCY_PAY, AGENCY_STATUS, AGENCY_CONTENT, 
						AGENCY_VEIWS, AGENCY_KEYWORD, AGENCY_OPTION, 
						MEMBER_ID, SCORE_BUY, SCORE_BUY_COUNT, SCORE_BUY_RATE, 
						SCORE_SELL, SCORE_SELL_COUNT, SCORE_SELL_RATE, ITEMFILENAME 
				from AGENCY, SCORE, (
					select ITEMFILENAME 
					from ITEMLIST
					where ITEMLIST_NO = (
						select ITEMLIST_NO
						from MY_ITEM
						where MYITEM_NO = (
							select MYITEM_NO 
							from (
								select MYITEM_NO 
								from (
									select MYITEM_NO 
									from USING_ITEM 
									where MYITEM_NO 
									in (
										select MYITEM_NO 
										from MY_ITEM, agency 
										where MEMBER_ID = agency_writer 
										and ITEMLIST_NO 
										in (
											select ITEMLIST_NO 
											from itemlist 
											where ITEMTYPE = 2 
											and MYITEM_STATUS ! = 2
										)
									)
								) order by rownum desc
							) where rownum= 1
						)
					)
				)
				where AGENCY_TITLE like '%'||#{agency_title}||'%' 
				and agency_type = 2 
				and agency_writer = member_id 
				order by AGENCY_NO desc
			) AG 
		)
		where rn between #{slavePageBegin} and #{slavePageEnd}


	select ITEMFILENAME 
	from ITEMLIST
	where ITEMLIST_NO = (
		select ITEMLIST_NO
		from MY_ITEM
		where MYITEM_NO = (
			select MYITEM_NO 
			from (
				select MYITEM_NO 
				from (
					select MYITEM_NO 
					from USING_ITEM 
					where MYITEM_NO 
					in (
						select MYITEM_NO 
						from MY_ITEM 
						where MEMBER_ID = #{ agency_writer } 
						and ITEMLIST_NO 
						in (
							select ITEMLIST_NO 
							from itemlist 
							where ITEMTYPE = 2 
							and MYITEM_STATUS ! = 2
						)
					)
				) order by rownum desc
			) where rownum= 1
		)
	)
